import React from "react";
import { ReactDOM } from "react";
import { Map, Marker, InfoWindow } from "react-amap";

class MapContent extends React.Component {
  constructor() {
    super();
    this.state = {
      position: { longitude: 116.4, latitude: 39.9 },
      center: { longitude: 116.4, latitude: 39.9 },
      draggable: true,
    };
    this.mapPlugins = ["ToolBar", "Scale"];
    this.markerEvents = {
      click: () => {
        console.log("marker clicked!");
      },
    };
  }

  randomPosition() {
    this.setState({
      position: {
        longitude: 120 + Math.random() * 10,
        latitude: 35 + Math.random() * 10,
      },
    });
  }

  toggleDraggable() {
    this.setState({
      draggable: !this.state.draggable,
    });
  }

  render() {
    return (
      <div>
        <div className=" w-screen h-screen bg-gray-100 overflow-hidden">
          <Map
            plugins={this.mapPlugins}
            center={this.state.position}
            ampakey={"5006aba1c28995bc84672dca708fc2d8"}
            zoom={12}
          >
            <Marker
              events={this.markerEvents}
              position={this.state.position}
              draggable={this.state.draggable}
            />
            <InfoWindow
              position={this.state.position}
              visible={this.state.visible}
              isCustom={false}
              content={html}
              size={this.state.size}
              offset={this.state.offset}
              events={this.windowEvents}
            />
          </Map>
        </div>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white text-sm font-bold py-1 px-3 rounded"
          onClick={() => {
            this.randomPosition();
          }}
        >
          Position
        </button>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white text-sm font-bold py-1 px-3 rounded"
          onClick={() => {
            this.toggleDraggable();
          }}
        >
          Draggable
        </button>
      </div>
    );
  }
}
export default MapContent;
