import React, { useRef, useState } from "react";
import { APILoader, Map, Geolocation, Marker } from "@uiw/react-amap";
import { initializeApp } from "firebase/app";
import {
  getStorage,
  ref,
  uploadString,
  getDownloadURL,
} from "firebase/storage";

// Initialize Firebase. Replace these values with your actual Firebase config.
const firebaseConfig = {
  // Your Firebase Config
};

const app = initializeApp(firebaseConfig);
const storage = getStorage(app);

const MapComponent = () => {
  const mapRef = useRef(null);
  const [userId, setUserId] = useState("");
  const [address, setAddress] = useState("");
  const [markerPosition, setMarkerPosition] = useState({
    longitude: 0,
    latitude: 0,
  });

  const geolocationEvents = {
    complete: (data) => {
      setMarkerPosition({
        longitude: data.position.lng,
        latitude: data.position.lat,
      });
    },
  };

  const handleMapLoaded = ({ AMap, map }) => {
    mapRef.current = map;
    const geocoder = new AMap.Geocoder({});

    AMap.event.addListener(map, "moveend", () => {
      const center = map.getCenter();
      setMarkerPosition({ longitude: center.lng, latitude: center.lat });

      geocoder.getAddress(center, function (status, result) {
        if (status === "complete" && result.info === "OK") {
          setAddress(result.regeocode.formattedAddress);
        }
      });
    });
  };

  const handleSubmit = async () => {
    try {
      const storageRef = ref(storage, `locations/${userId}`);
      await uploadString(storageRef, address);
      alert("Data submitted successfully");
    } catch (error) {
      console.error("Error uploading data:", error);
    }
  };

  return (
    <div>
      <input
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="Enter User ID"
      />
      <Map
        center={markerPosition}
        zoom={13}
        amapkey="your-amap-key"
        version="1.4.15"
        onInstanceCreated={handleMapLoaded}
      >
        <Geolocation events={geolocationEvents} />
        <Marker position={markerPosition} />
      </Map>
      <button onClick={handleSubmit}>Submit</button>
      <div>Address: {address}</div>
    </div>
  );
};

const MapContainer = () => (
  <APILoader version="2.0.5" akey="a7a90e05a37d3f6bf76d4a9032fc9129">
    <MapComponent />
  </APILoader>
);

export default MapContainer;
