import React, { useState, useEffect } from "react";
import AMapLoader from "@amap/amap-jsapi-loader";
import { ref, uploadString } from "firebase/storage";
import { db, storage } from "../firebase";
import {
  serverTimestamp,
  collection,
  doc,
  addDoc,
  setDoc,
} from "firebase/firestore";

const MapComponent = () => {
  const [userId, setUserId] = useState("");
  const [address, setAddress] = useState("");
  const [markerPosition, setMarkerPosition] = useState({
    longitude: 0,
    latitude: 0,
  });

  useEffect(() => {
    AMapLoader.load({
      key: "5006aba1c28995bc84672dca708fc2d8",
      version: "2.0",
      plugins: ["AMap.Geocoder", "AMap.Geolocation"],
    })
      .then((AMap) => {
        const map = new AMap.Map("container", {
          zoom: 13,
          resizeEnable: true,
        });

        const marker = new AMap.Marker({
          map: map,
        });

        const geocoder = new AMap.Geocoder({});

        const geolocation = new AMap.Geolocation({
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 0,
          convert: true,
          showButton: true,
          buttonPosition: "RB",
          buttonOffset: new AMap.Pixel(10, 20),
          showMarker: false,
          showCircle: true,
          panToLocation: true,
          zoomToAccuracy: true,
        });

        map.addControl(geolocation);

        geolocation.watchPosition((status, result) => {
          if (status === "complete") {
            marker.setPosition(result.position);
            setMarkerPosition({
              longitude: result.position.lng,
              latitude: result.position.lat,
            });
          } else {
            console.log("Geolocation failed: " + result.message);
          }
        });

        // Event on map drag end
        AMap.event.addListener(map, "dragend", () => {
          const center = map.getCenter();
          marker.setPosition(center);
          setMarkerPosition({ longitude: center.lng, latitude: center.lat });

          geocoder.getAddress(
            [center.lng, center.lat],
            function (status, result) {
              if (status === "complete" && result.info === "OK") {
                setAddress(result.regeocode.formattedAddress);
              }
            }
          );
        });
      })
      .catch((e) => console.error(e));
  }, []);

  const handleSubmit = async () => {
    try {
      const storageRef = ref(storage, `locations/${userId}`);
      await uploadString(storageRef, address);
      //new Date(timestamp?.toDate()).toLocaleString()
      alert("Data submitted successfully");
    } catch (error) {
      console.error("Error uploading data:", error);
    }
  };

  return (
    <div className="h-screen bg-gray-100 overflow-hidden">
      <div className=" mx-auto flex justify-center mt-20">
        <input
          className=" text-lg rounded-lg px-3"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          placeholder="Enter User ID"
        />
      </div>
      <div id="container" style={{ width: "100%", height: "400px" }}></div>
      <button onClick={handleSubmit}>Submit</button>
      <div>Address: {address}</div>
    </div>
  );
};

export default MapComponent;
