import React, { useState, useEffect } from "react";
import AMapLoader from "@amap/amap-jsapi-loader";
import { initializeApp } from "firebase/app";
import { getStorage, ref, uploadString } from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyB3p-yiWJEFyxN_qJeDHOw3FKy3KtrgpQA",
  authDomain: "streetview-app-5cc5a.firebaseapp.com",
  projectId: "streetview-app-5cc5a",
  storageBucket: "streetview-app-5cc5a.appspot.com",
  messagingSenderId: "820610466111",
  appId: "1:820610466111:web:3f7dd70579f8a75a5d938a",
  measurementId: "G-8EMLDPGBSM",
};

const app = initializeApp(firebaseConfig);
const storage = getStorage(app);

const MapComponent = () => {
  const [userId, setUserId] = useState("");
  const [address, setAddress] = useState("");
  const [markerPosition, setMarkerPosition] = useState({
    longitude: 0,
    latitude: 0,
  });

  useEffect(() => {
    AMapLoader.load({
      key: "5006aba1c28995bc84672dca708fc2d8",
      version: "2.0",
      plugins: ["AMap.Geocoder", "AMap.Geolocation"],
    })
      .then((AMap) => {
        const map = new AMap.Map("container", {
          zoom: 13,
          resizeEnable: true,
        });

        const marker = new AMap.Marker({
          map: map,
        });

        // Add Geolocation plugin
        const geolocation = new AMap.Geolocation({
          enableHighAccuracy: true, // Whether to use the high-precision positioning mode
          timeout: 10000, // Overdue time, default is 10s
          maximumAge: 0, // Location result caching time, default is 0, do not cache
          convert: true, // Automatically convert positioning results to longitude and latitude
          showButton: true, // Display location button
          buttonPosition: "RB", // Button position
          buttonOffset: new AMap.Pixel(10, 20), // Button offset
          showMarker: true, // Display the positioning marker
          showCircle: true, // Display precision circle
          panToLocation: true, // Positioning after the automatic move map
          zoomToAccuracy: true, // Positioning after automatically zooming in on the map
        });

        map.addControl(geolocation);

        geolocation.getCurrentPosition((status, result) => {
          if (status === "complete") {
            setMarkerPosition({
              longitude: result.position.lng,
              latitude: result.position.lat,
            });
          } else {
            console.log("Geolocation failed: " + result.message);
          }
        });

        const geocoder = new AMap.Geocoder({});

        // Move the addListener call inside the then block
        AMap.event.addListener(map, "moveend", () => {
          const center = map.getCenter();
          setMarkerPosition({ longitude: center.lng, latitude: center.lat });

          geocoder.getAddress(
            [center.lng, center.lat],
            function (status, result) {
              if (status === "complete" && result.info === "OK") {
                setAddress(result.regeocode.formattedAddress);
              }
            }
          );
        });
      })
      .catch((e) => console.error(e));
  }, []);

  const handleSubmit = async () => {
    try {
      const storageRef = ref(storage, `locations/${userId}`);
      await uploadString(storageRef, address);
      alert("Data submitted successfully");
    } catch (error) {
      console.error("Error uploading data:", error);
    }
  };

  return (
    <div>
      <input
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="Enter User ID"
      />
      <div id="container" style={{ width: "100%", height: "400px" }}></div>
      <button onClick={handleSubmit}>Submit</button>
      <div>Address: {address}</div>
    </div>
  );
};

export default MapComponent;
